import React, { Component } from "react";
import axios from "axios";
import "./MainJobBoard.scss";

export default class MainJobBoard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      jobs: [],
      dropdownIsOpen: true,
      dropdownId: 0,
      prevDropdownId: 0
    };
    this.handleDropdown = this.handleDropdown.bind(this);
    // this.dropdownId = this.dropdownId.bind(this);
  }

  componentDidMount(e) {
    axios.get("/getjobs").then(res => {
      this.setState({ jobs: res.data });
    });
  }

  handleDropdown(e) {
    this.setState({ dropdownIsOpen: !this.state.dropdownIsOpen });
    this.setState(prevState => ({
      prevDropdownId: prevState.dropdownId
    }));
    console.log(this.state.dropdownIsOpen);
    console.log(this.state.prevDropdownId);
    console.log(hey);
    console.log(this.state.dropdownId);
  }

  render() {
    return (
      <div onClick={this.handleDropdown}>
        {this.state.jobs.map((val, index) => {
          return (
            // <div
            //   onClick={() =>
            //     this.setState({ prevDropdownId: prevState.dropdownId })
            //   }
            // >
            <div onClick={this.handleDropdown}>
              <div
                className="all-jobs"
                // onClick={this.handleDropdown}
                onClick={() => this.setState({ dropdownId: val.id })}
              >
                <h1>{"dropdownId:" + this.state.dropdownId}</h1>
                {/* <h1>{val.id}</h1> */}
                <div className="pre-dropdown-container">
                  <div className="company-logo-div">
                    <img className="company-logo" src={val.company_logo}></img>
                  </div>
                  <div className="title-and-company-div">
                    <div className="title-div">{val.job_name}</div>
                    <div className="company-div">
                      <p className="company-paragraph">{val.company_name}</p>
                      <p> | </p>
                      <p className="location-paragraph">
                        <strong>{val.job_location}</strong>
                      </p>
                    </div>
                  </div>
                  <div className="days-ago-div">{val.days_since_posted}</div>
                  <div className="apply-now-div">
                    <form action={val.company_email} target="_blank">
                      <button type="submit" className="apply-now-button">
                        Apply
                      </button>
                    </form>
                  </div>
                </div>
                {/* <div className="content"></div> */}
                <div
                  className={
                    "myclass-" + (this.state.dropdownId === val.id ? val.id : 0)
                  }
                >
                  {/* {this.state.openDropdown === true ? ( */}
                  {this.state.dropdownId === val.id &&
                  this.state.dropdownIsOpen === false &&
                  this.state.prevDropdownId === val.id ? (
                    <h1 className="do-not-display"></h1>
                  ) : (
                    <h3>{"val.id= " + val.id + " " + val.job_description}</h3>
                  )}
                </div>
              </div>
            </div>
            // </div>
          );
        })}
      </div>
    );
  }
}
